publications.each do |pub|
  authors = pub[0]
  title = pub[1]
  review = pub[2]
  year = pub[2].scan(/\d{4}/)[0]
  record = Refinery::Publications::Publication.find_or_initialize_by_title(title)
  if record.new_record?
    puts "creating new pub: #{title}"
    record.authors = authors
    record.review = review
    record.year = year.to_i
    record.save!
  else
    puts "skipping existing pub: #{title}"
  end
end


Refinery::I18n.frontend_locales.each do |lang|
  I18n.locale = lang

  if defined?(Refinery::User)
    Refinery::User.all.each do |user|
      if user.plugins.where(:name => 'refinerycms-publications').blank?
        user.plugins.create(:name => 'refinerycms-publications',
                            :position => (user.plugins.maximum(:position) || -1) +1)
      end
    end
  end

  url = "/publications"
  if defined?(Refinery::Page) && Refinery::Page.where(:link_url => url).empty?
    page = Refinery::Page.create(
      :title => 'Collaborative studies',
      :link_url => url,
      :deletable => false,
      :menu_match => "^#{url}(\/|\/.+?|)$"
    )
    Refinery::Pages.default_parts.each_with_index do |default_page_part, index|
      page.parts.create(:title => default_page_part, :body => nil, :position => index)
    end
  end
end
